package DDG::Goodie::Pi;

use DDG::Goodie;

name 'Pi';
description 'Shows the value of pi (≤1000 digits)';
primary_example_queries '10 digits of pi';
category 'calculations';
topics 'math';
code_url 'https://github.com/yzwx/zeroclickinfo-goodies/blob/pi/lib/DDG/Goodie/Pi.pm';
attribution github => ['https://github.com/yzwx', 'yzwx'];

triggers end => 'digits of pi';

zci answer_type => 'pi';
zci is_cached => 1;

my $pi = '3.';

handle remainder => sub {
	my $random = int(rand(1000)) + 1;

    return substr $pi, 0, $_ + 1 if $_ =~ /^\d+$/ && $_ >= 3 && $_ <= 1000; # Integer
    return $pi if $_ eq ''; # "digits of pi"
    return substr $pi, 0, 4 if lc $_ eq 'random' && $random < 3; # Random integer <3
    return substr $pi, 0, $random + 1 if lc $_ eq 'random'; # Random integer ≥3 but ≤1000
    return;
};


1;